import{_ as n}from"./international-img-2132706b.js";import{_ as r,c as i,b as s,e as d,f as t,s as l,g as c,o as _,i as v,p,h as g}from"./index-055e4384.js";const h={},b=a=>(p("data-v-059712d5"),a=a(),g(),a),m={class:"internationnal"},x=b(()=>s("img",{class:"headimg",src:n,alt:""},null,-1)),f={class:"centerbody"},B={class:"wrapper"},u=l('<div style="margin-top:40px;" class="card" data-v-059712d5><div class="card-text" data-v-059712d5><div class="card-text-head" data-v-059712d5><strong data-v-059712d5>GB/T 39604-2020《社会责任管理体系 要求及使用指南》</strong></div><div class="card-text-content" data-v-059712d5><p data-v-059712d5>     《社会责任管理体系—要求及使用指南》（GB/T 39604-2020）是2020年12月14日实施的一项中华人民共和国国家标准，归口于中国标准化研究院。 [1]  《社会责任管理体系—要求及使用指南》（GB/T 39604-2020）规定了社会责任管理体系要求，并给出了其使用指南，以使组织能够通过防止和控制不良影响、促进有益影响以及主动改进其社会责任绩效来更好地履行其社会责任，从而成为对社会更负责任的组织。该标准适用于任何具有以下愿望的组织：通过建立、实施和保持社会责任管理体系，以改进社会责任管理、消除不良影响因素并最大限度地降低不良影响（包括体系缺陷等）、在可行时主动促进有益影以及应对与其决策和活动相关的社会责任管理体系的不符合。 [2] </p></div><div class="card-text-link" data-v-059712d5><a href="https://openstd.samr.gov.cn/bzgk/gb/newGbInfo?hcno=27B19A8F57650DD5DCF1109AB3029C7D" target="_blank" data-v-059712d5>点击跳转</a></div></div></div><div class="card" data-v-059712d5><div class="card-text" data-v-059712d5><div class="card-text-head" data-v-059712d5><strong data-v-059712d5>GB/T 26317-2010《公司治理风险管理指南》</strong></div><div class="card-text-content" data-v-059712d5><p data-v-059712d5>     《公司治理风险管理指南》是2011年5月1日实施的一项中国国家标准。 </p></div><div class="card-text-link" data-v-059712d5><a href="https://openstd.samr.gov.cn/bzgk/gb/newGbInfo?hcno=E3BB3D4703A4CD330303F28F416F5969" target="_blank" data-v-059712d5>点击跳转</a></div></div></div><div class="card" data-v-059712d5><div class="card-text" data-v-059712d5><div class="card-text-head" data-v-059712d5><strong data-v-059712d5>GB/T36000-2015《社会责任指南》</strong></div><div class="card-text-content" data-v-059712d5><p data-v-059712d5>     《社会责任指南》（GB/T36000-2015）是2016年1月1日实施的一项中华人民共和国国家标准，归口于中国标准化研究院。 [1]  《社会责任指南》（GB/T36000-2015）为组织理解社会责任并管理和实施相关活动提供指南，旨在帮助组织在遵守法律法规和基本道德规范的基础上实现更高的组织社会价值，最大限度地致力于可持续发展。该标准适用于所有类型的组织。在应用本标准时，建议组织充分考虑自身规模、性质、行业特征等实际状况和条件。 [2] </p></div><div class="card-text-link" data-v-059712d5><a href="https://openstd.samr.gov.cn/bzgk/gb/newGbInfo?hcno=83344903824DCAB8C107D5350077EE2E" target="_blank" data-v-059712d5>点击跳转</a></div></div></div><div class="card" data-v-059712d5><div class="card-text" data-v-059712d5><div class="card-text-head" data-v-059712d5><strong data-v-059712d5>生态设计产品评价通则</strong></div><div class="card-text-content" data-v-059712d5><p data-v-059712d5>     《生态设计产品评价通则》（GB/T 32161-2015）是2016年5月1日实施的一项中华人民共和国国家标准，归口于全国环境管理标准化技术委员会。 [1]  《生态设计产品评价通则》（GB/T 32161-2015）规定了生态设计产品评价的术语和定义、评价原则和方法、评价要求、生命周期评价报告编制方法。《生态设计产品评价通则》（GB/T 32161-2015）适用于具体生态设计产品评价规范的编制。 [2] </p></div><div class="card-text-link" data-v-059712d5><a href="https://openstd.samr.gov.cn/bzgk/gb/newGbInfo?hcno=443E905A88FCF5D1FC7182413F67AB11" target="_blank" data-v-059712d5>点击跳转</a></div></div></div>',4);function k(a,G){const e=c("el-breadcrumb-item"),o=c("el-breadcrumb");return _(),i("div",m,[x,s("div",f,[s("div",B,[d(o,{separator:"/"},{default:t(()=>[d(e,{to:{path:"/"}},{default:t(()=>[v("首页")]),_:1}),d(e,null,{default:t(()=>[v("国内标准")]),_:1})]),_:1}),u])])])}const D=r(h,[["render",k],["__scopeId","data-v-059712d5"]]);export{D as default};
